#info = audioinfo('./testinga.wav');tmp_file = './testinga.wav';[data,sample] = audioread(tmp_file);data = double(data);subplot(331);plot(data);title('origion data');data = filter([1 -0.9375],1,data); #预加��subplot(222);plot(data);title('pre-emphasis data');[frame,t] = enframe(data,256,80);bank = melbankm(24,256,sample,0,0.4,'t'); %Mel滤波器的阶数��4，FFT变换的长度为256，采样频率为16000Hz %归一化Mel滤波器组系数  bank = full(bank); %full() convert sparse matrix to full matrixbank = bank/max(bank(:));w = 1+6*sin(pi*[1:12]./12);%归一化倒谱提升窗口w = w/max(w);for k = 1:12    n = 0:23;      dctcoef(k,:) = cos((2*n+1)*k*pi/(2*24));  endfor i = 1:size(frame,1)  y = frame(i,:);  s = y' .* hamming(256);  t = abs(fft(s));  t = t.^2;  c1 = dctcoef*log(bank*t(1:129));  c2 = c1.*w';    m(i,:) = c2;endsubplot(332);plot(m);title('melbankm data');mel_comp(m,m)%��ȡһ�ײ���ϵ��  dtm = zeros(size(m));  for i = 3:size(m,1)-2  dtm(i,:) = -2*m(i-2,:)-m(i-1,:)+m(i+1,:)+2*m(i+2,:);  enddtm = dtm/3;%��ȡ���ײ���ϵ��dtmm = zeros(size(dtm));for i = 3:size(dtm,1)-2 dtmm(i,:)=-2*dtm(i-2,:)-dtm(i-1,:)+dtm(i+1,:)+2*dtm(i+2,:);enddtmm = dtmm/3;%�ϲ�mfcc������һ�ס����ײ���mfcc����ccc=[m dtm dtmm];%ȥ����β�֡����Ϊ���֡��һ�ײ��ֲ���Ϊ0ccc = ccc(3:size(m,1)-2,:);subplot(333);plot(ccc(:,1));title('ccc data');%��ÿһ֡������ �� ������cr = zeros(2,size(frame,1));for i = 1:size(frame,1)  y = frame(i,:);  [c,r] = centeroid(y,32);  cr(1,i) = c;  cr(2,i) = r;endsubplot(334);plot(cr(1,:));title('center data');subplot(335);plot(cr(2,:));title('sqrt data');r = compare_t(cr(1,:),cr(1,:))%fft_data = fft(data);%subplot(223);plot(fft_data);title('fft data');