#info = audioinfo('./testinga.wav');tmp_file = './testinga.wav';[data,sample] = audioread(tmp_file);data = double(data);subplot(221);plot(data);title('origion data');data = filter([1 -0.9375],1,data); #预加重subplot(222);plot(data);title('pre-emphasis data');[frame,t] = enframe(data,256,80);bank = melbankm(24,256,sample,0,0.4,'t'); %Mel滤波器的阶数为24，FFT变换的长度为256，采样频率为16000Hz %归一化Mel滤波器组系数  bank = full(bank); %full() convert sparse matrix to full matrixbank = bank/max(bank(:));w = 1+6*sin(pi*[1:12]./12);%归一化倒谱提升窗口  w = w/max(w);for k = 1:12    n = 0:23;      dctcoef(k,:) = cos((2*n+1)*k*pi/(2*24));  end  for i = 1:size(frame,1)  y = frame(i,:);  s = y' .* hamming(256);  t = abs(fft(s));  t = t.^2;  c1 = dctcoef*log(bank*t(1:129));  c2 = c1.*w';    m(i,:) = c2;endsubplot(223);plot(frame);title('frame data');subplot(224);plot(m);title('melbankm data');%fft_data = fft(data);%subplot(223);plot(fft_data);title('fft data');