#info = audioinfo('./testinga.wav');tmp_file = './testinga.wav';[data,sample] = audioread(tmp_file);data = double(data);subplot(331);plot(data);title('origion data');data = filter([1 -0.9375],1,data); #棰subplot(222);plot(data);title('pre-emphasis data');[frame,t] = enframe(data,256,80);bank = melbankm(24,256,sample,0,0.4,'t'); %Mel婊ゆ尝ㄧ舵颁4锛FFT㈢垮害涓256锛烽涓16000Hz %褰涓Mel婊ゆ尝ㄧ绯绘  bank = full(bank); %full() convert sparse matrix to full matrixbank = bank/max(bank(:));w = 1+6*sin(pi*[1:12]./12);%褰涓璋辨绐w = w/max(w);for k = 1:12    n = 0:23;      dctcoef(k,:) = cos((2*n+1)*k*pi/(2*24));  endfor i = 1:size(frame,1)  y = frame(i,:);  s = y' .* hamming(256);  t = abs(fft(s));  t = t.^2;  c1 = dctcoef*log(bank*t(1:129));  c2 = c1.*w';    m(i,:) = c2;endsubplot(332);plot(m);title('melbankm data');%求取一阶差分系数  dtm = zeros(size(m));  for i = 3:size(m,1)-2  dtm(i,:) = -2*m(i-2,:)-m(i-1,:)+m(i+1,:)+2*m(i+2,:);  enddtm = dtm/3;%求取二阶差分系数dtmm = zeros(size(dtm));for i = 3:size(dtm,1)-2 dtmm(i,:)=-2*dtm(i-2,:)-dtm(i-1,:)+dtm(i+1,:)+2*dtm(i+2,:);enddtmm = dtmm/3;%合并mfcc参数和一阶、二阶差分mfcc参数ccc=[m dtm dtmm];%去除首尾两帧，因为这两帧的一阶差分参数为0ccc = ccc(3:size(m,1)-2,:);subplot(333);plot(ccc(:,1));title('ccc data');%求每一帧的质心 和 均方跟cr = zeros(2,size(frame,1));for i = 1:size(frame,1)  y = frame(i,:);  [c,r] = centeroid(y,32);  cr(1,i) = c;  cr(2,i) = r;endsubplot(334);plot(cr(1,:));title('center data');subplot(335);plot(cr(2,:));title('sqrt data');%fft_data = fft(data);%subplot(223);plot(fft_data);title('fft data');