#info = audioinfo('./testinga.wav');tmp_file = './hello.wav';tmp1_file = './hello1.wav';[data,sample] = audioread(tmp_file);[d1,s1] = audioread(tmp1_file);data = double(data);d1 = double(d1);subplot(441);plot(data);title('origion1 data');subplot(445);plot(d1);title('origion2 data');data = filter([1 -0.9375],1,data);  #预加重d1 = filter([1 -0.9375],1,d1);      #预加重subplot(442);plot(data);title('pre-emphasis data');subplot(446);plot(d1);title('pre-emphasis d1');[frame,t] = enframe(data,256,80);   #分帧[f1,t] = enframe(d1,256,80);        #分帧m = creat_mel(frame,sample);m1 = creat_mel(f1,s1);subplot(443);plot(m);title('melbankm data');subplot(447);plot(m1);title('melbankm data');%求取质心 和 均值cr = zeros(2,size(frame,1));for i = 1:size(frame,1)  y = frame(i,:);  [c,r] = centeroid(y,32);  cr(1,i) = c;  cr(2,i) = r;endcr1 = zeros(2,size(f1,1));for i = 1:size(f1,1)  y = f1(i,:);  [c,r] = centeroid(y,32);  cr1(1,i) = c;  cr1(2,i) = r;endsubplot(444);plot(cr(1,:));title('center data');subplot(448);plot(cr1(1,:));title('center1 data');subplot(4,4,9);plot(cr(2,:));title('zhixin data');subplot(4,4,13);plot(cr1(2,:));title('zhixin1 data');