function f = mel_comp(mel1,mel2)%进行倒浦谱系数对比%mel1 样本系数%mel2 待测系数  %计算每一个系数的均值  f = 0;  a1 = zeros(1,size(mel1,2));  a2 = zeros(1,size(mel2,2));  for i = 1:size(mel1,2)    y1 = mel1(:,i);    y2 = mel2(:,i);    t1 = t2 = 0;    for j = 1:length(y1)      t1 = t1 + y1(j);      t2 = t2 + y2(j);    end    a1(i) = t1/ length(y1);    a2(i) = t2/ length(y2);  end  %计算补偿后的系数  for i = 1:size(mel1,1)    ma1 = mel1(i,:);    ma2 = mel2(i,:);    mel1(i,:) = ma1 - a1;    mel2(i,:) = ma2 - a2;  end    %计算每一帧的欧式距离  disps = zeros(size(mel1,1));  for i = 1:size(mel1,1)    ma1 = mel1(i,:);    ma2 = mel2(i,:);    disp = 0;    for j = 1:length(ma1)      disp = disp + (ma1(j) - ma2(j))^2;    end    if disp == 0      disps(i) = 0;    else      disps(i) = sqrt(disp);    end  end  %计算所有帧的平均欧式距离  for i = 1:length(disps)    f = f + disps(i);   end  f = f / length(disps);end