function f = mel_comp(mel1,mel2)%进行倒浦谱系数对比%mel1 样本系数%mel2 待测系数  %计算每一个系数的均值  f = 0;  for i = 1:size(mel1,1)    y1 = mel1(i,:);    y2 = mel2(i,:);	a1(i) = sum(y1)/length(y1);    a2(i) = sum(y2)/length(y2);  end  %计算补偿后的系数  for i = 1:size(mel1,1)    ma1 = mel1(i,:);    ma2 = mel2(i,:);    mel1(i,:) = ma1 - a1(i);    mel2(i,:) = ma2 - a2(i);  end    %计算每一帧的欧式距离  for i = 1:size(mel1,1)    ma1 = mel1(i,:);    ma2 = mel2(i,:);	disp = sum((ma1.^2 - ma2.^2)) / size(mel1,2);    if disp == 0      disps(i) = 0;    else      disps(i) = sqrt(disp);    end  end  %计算所有帧的平均欧式距离  f = sum(disps) / length(disps);end